import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './loginpage.css';
import logo from '../../logo.svg';
import defaultPfp from '../assets/defaultpfp.jpg'; 
const Navbar = ({ onLoginClick, onSigninClick }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    const storedLogin = localStorage.getItem('isLoggedIn');
    const storedRole = localStorage.getItem('userRole');
    setIsLoggedIn(storedLogin === 'true');
    setUserRole(storedRole);
  }, []);

  const profileLink = userRole === 'ngo' ? '/ngo-profile' : '/volunteer-profile';

  return (
    <nav className="navbar">
      <div className="logo">
        <img src={logo} alt="Logo" />
      </div>

      <ul className="nav-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/volunteer">Volunteer</Link></li>
        <li><Link to="/donation">Donation</Link></li>
      </ul>

      <div className="button-container">
        {isLoggedIn ? (
          <Link to={profileLink}>
            <img src={defaultPfp} alt="Profile" className="profile-icon" />
          </Link>
        ) : (
          <>
            <button className="button login-btn" onClick={onLoginClick}>Login</button>
            <button className="button signup-btn" onClick={onSigninClick}>Sign Up</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
