import React from "react";
import {
  LogOut,
  History,
  Settings,
  PlusSquare,
  UserRound,
} from "lucide-react";
import defaultPfp from "../../assets/defaultPfp.jpg";

const VolunteerProfile = () => {
  const user = JSON.parse(localStorage.getItem("user") || "{}");
  const avatarUrl = user.profilePic || defaultPfp;

  return (
    <div className="max-w-5xl mx-auto p-6 bg-blue-50 rounded-xl shadow-md border border-blue-200 mt-10">
      {/* Profile Header */}
      <div className="flex items-center gap-6 mb-8">
        <div className="w-24 h-24 rounded-full overflow-hidden border-4 border-blue-300 shadow">
          <img src={avatarUrl} alt="Profile" className="w-full h-full object-cover" />
        </div>
        <div>
          <h1 className="text-3xl font-bold text-blue-900">{user.name || "Volunteer"}</h1>
          <p className="text-sm text-blue-700 mt-1">{user.email || "No email provided"}</p>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="flex flex-wrap gap-3 mb-6">
        <ActionButton icon={<UserRound className="w-4 h-4" />} label="My Profile" />
        <ActionButton icon={<PlusSquare className="w-4 h-4" />} label="Join Events" />
        <ActionButton icon={<History className="w-4 h-4" />} label="Volunteer History" />
        <ActionButton icon={<Settings className="w-4 h-4" />} label="Account Settings" />
        <button
          onClick={() => {
            localStorage.clear();
            window.location.href = "/";
          }}
          className="flex items-center gap-2 text-sm font-medium text-white bg-red-500 hover:bg-red-600 px-4 py-2 rounded-full transition shadow"
        >
          <LogOut className="w-4 h-4" /> Sign Out
        </button>
      </div>

      {/* About Section */}
      <div className="bg-white p-5 rounded-xl border border-blue-100 shadow-sm">
        <h2 className="text-xl font-semibold text-blue-900 mb-4">About Me</h2>
        <ul className="text-blue-800 space-y-2 text-base">
          <li><strong>ðŸ§  Skills:</strong> {user.skills || "N/A"}</li>
          <li><strong>ðŸ•’ Availability:</strong> {user.availability || "Not Provided"}</li>
          <li><strong>ðŸ“‹ Experience:</strong> {user.experience || "No experience yet"}</li>
        </ul>
      </div>
    </div>
  );
};

const ActionButton = ({ icon, label }) => (
  <button className="flex items-center gap-2 text-sm font-medium text-blue-800 hover:text-white px-4 py-2 bg-blue-100 hover:bg-blue-600 border border-blue-300 rounded-full transition shadow-sm">
    {icon}
    {label}
  </button>
);

export default VolunteerProfile;
