import React from "react";
import {
  LogOut,
  History,
  Settings,
  PlusSquare,
  GraduationCap,
} from "lucide-react";
import defaultPfp from "../../assets/defaultPfp.jpg";

const NGOProfile = () => {
  const user = JSON.parse(localStorage.getItem("user") || "null");

  if (!user) {
    return (
      <div className="text-center py-20 text-blue-800 text-lg font-medium">
        User data not found. Please log in again.
      </div>
    );
  }

  const avatarUrl = user.logo || defaultPfp;

  return (
    <div className="px-6 py-10 max-w-5xl mx-auto bg-gradient-to-b from-blue-50 to-blue-100 rounded-2xl shadow-xl border border-blue-200">
      {/* Top Profile Section */}
      <div className="flex flex-col sm:flex-row items-center gap-6 mb-8">
        <div className="w-28 h-28 rounded-full border-4 border-blue-300 overflow-hidden shadow-md">
          <img src={avatarUrl} alt="NGO Logo" className="w-full h-full object-cover" />
        </div>
        <div className="text-center sm:text-left">
          <h1 className="text-4xl font-bold text-blue-900">{user.ngoName || "NGO"}</h1>
          <p className="text-md text-blue-700 mt-2">{user.email || "Email not available"}</p>
        </div>
      </div>

      {/* Button Actions */}
      <div className="flex flex-wrap justify-start gap-3 mb-8">
        <ActionButton icon={<GraduationCap className="w-4 h-4" />} label="NGO Profile" />
        <ActionButton icon={<PlusSquare className="w-4 h-4" />} label="Add Events" />
        <ActionButton icon={<History className="w-4 h-4" />} label="Past Events" />
        <ActionButton icon={<Settings className="w-4 h-4" />} label="Account Settings" />
        <button
          onClick={() => {
            localStorage.clear();
            window.location.href = "/";
          }}
          className="flex items-center gap-2 text-sm font-medium text-white bg-red-500 hover:bg-red-600 px-4 py-2 rounded-full transition shadow-sm"
        >
          <LogOut className="w-4 h-4" /> Sign Out
        </button>
      </div>

      {/* NGO Info Section */}
      <section className="bg-white rounded-xl p-6 shadow-sm border border-blue-100">
        <h2 className="text-2xl font-semibold text-blue-900 mb-4">NGO Info</h2>
        <ul className="text-base text-blue-800 space-y-2 pl-1">
          <li><strong>üìç Address:</strong> {user.address || "Not provided"}</li>
          <li><strong>üéØ Mission:</strong> {user.mission || "No mission set"}</li>
          <li><strong>üìÅ Current Projects:</strong> {user.projects?.join(', ') || "None"}</li>
        </ul>
      </section>
    </div>
  );
};

// ‚úÖ Reusable Button Component (no TypeScript)
const ActionButton = ({ icon, label }) => (
  <button className="flex items-center gap-2 text-sm font-medium text-blue-800 hover:text-white px-4 py-2 bg-blue-100 hover:bg-blue-600 border border-blue-300 rounded-full transition shadow-sm">
    {icon} {label}
  </button>
);

export default NGOProfile;
